package com.p1;

public class frequency {

	public static void main(String[] args) {
		String str="banana";
		char ch='a';
		int count=0;
		for(int i=0;i<str.length();i++){
			if(str.charAt(i)==ch)
				count++;
		// TODO Auto-generated method stub
	}
System.out.println("frequency of:"+count);
	}
}
